import { Component } from "react";
import styles from "./Phonebook.module.css";
import { generate } from "shortid";
import ContactsList from "../ContactsList/ContactsList";

class Phonebook extends Component {
  state = {
    contacts: [
      { id: "id-1", name: "Rosie Simpson", number: "459-12-56" },
      { id: "id-2", name: "Hermione Kline", number: "443-89-12" },
      { id: "id-3", name: "Eden Clements", number: "645-17-79" },
      { id: "id-4", name: "Annie Copeland", number: "227-91-26" },
    ],
    filter: "",
    name: "",
    number: "",
  };

  nameInputId = generate();
  numberInputId = generate();

  handleSubmit = (e) => {
    e.preventDefault();
    const { name, number } = this.state;
    const { contacts } = this.state;
    this.setState((prevState) => {
      const newContact = [...prevState.contacts];
      newContact.push({ name: name, number: number });
      return { contacts: newContact, name: "", number: "" };
    });
  };

  handleChange = (e) => {
    const { name, value } = e.currentTarget;
    this.setState({
      [name]: value,
    });
  };

  filterItem = (e) => {
    this.setState({
      filter: e.target.value,
    });
  };

  filterInput = (e) => {
    const { contacts, filter } = this.state;
    const FilterLowerCase = filter.toLowerCase();
    return contacts
      .filter((item) => item.name.toLowerCase().includes(FilterLowerCase))
      .sort((a, b) => a.name.localeCompare(b.name));
  };

  render() {
    const { name, number, contacts } = this.state;
    const { handleChange, handleSubmit } = this;
    return (
      <>
        <h2 className={styles.Title}>Phonebook</h2>
        <form className={styles.Form} onSubmit={handleSubmit}>
          <div className={styles.FormContainer}>
            <div className={styles.Name}>
              <label className={styles.Label} htmlFor={this.nameInputId}>
                Name
                <input
                  className={styles.Input}
                  type="text"
                  name="name"
                  value={name}
                  pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
                  title="Имя может состоять только из букв, апострофа, тире и пробелов. Например Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan и т. п."
                  required
                  onChange={handleChange}
                  id={this.nameInputId}
                />
              </label>
            </div>
            <div className={styles.Tel}>
              <label className={styles.Label} htmlFor={this.numberInputId}>
                Number
                <input
                  className={styles.Input}
                  type="tel"
                  name="number"
                  value={number}
                  pattern="\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}"
                  title="Номер телефона должен состоять цифр и может содержать пробелы, тире, круглые скобки и может начинаться с +"
                  required
                  onChange={handleChange}
                  id={this.numberInputId}
                />
              </label>
            </div>
            <button type="submit" className={styles.NameBtn}>
              Add contact
            </button>
          </div>
        </form>
        <h2 className={styles.Title}>Contacts</h2>

        <ContactsList list={contacts} />
        <Filter
          value={filter}
          contacts={this.filterInput}
          onChange={filterItem}
        />
      </>
    );
  }
}

export default Phonebook;
